import React, { useState } from 'react';
import { NavHashLink as Link } from 'react-router-hash-link';
import { Navbar, Nav, Button } from 'react-bootstrap';
import styled from 'styled-components';
import { FaDiscord, FaTwitter, FaBars } from 'react-icons/fa';
import { AiOutlineClose } from 'react-icons/ai';
import Scrollspy from 'react-scrollspy';
import { useEffect } from "react";
import { connectWallet,
         getCurrentWalletConnected,
} from '../../utils/interact';


// https://codesandbox.io/s/o4ws2

const Styles = styled.div`

  .navbar-collapse {
    text-align: center;
    justify-content: flex-end;
  }

  .navbar-brand {
    color: yellow;
    &:hover {
      color: red;
    }
    margin-left: 25px;
  } 

  @media (max-width: 991.98px) {
    ul {
      display: flex;
      flex-direction: column;
    }
  }
 
  ul {
    margin-top: 15px;
    display: flex;
    justify-content: flex-end;

  }

  a, .navbar-nav .nav-link {
    color: white;
    font-weight: bold;
    -webkit-text-stroke: 0.5px black;
    padding: 10px;
    &:hover {
      color: yellow;
    }
    text-decoration: none;
  }

  .is-current {
    a{
        color: red;
        &::after {
            width: 100%;
        }
    }
  }

  .navbar {
    position: fixed;
    background-color: black;
    padding: 18px;
    top: 0;
    right: 0;
    left: 0;
    z-index: 1030;
  }

  // .bg-dark {
  //   position: fixed;
  //   background-color: black;
  // }

  .navbar-toggler {
    border-color: rgb(248, 186, 2);
  }
`;

const NavbarComp = () => {
  const [expanded, setExpanded] = useState(false);


  //DONT DUPLICATE IT HERE BUT MAKE ITONLY ONCE; ALSO FIX (CONNECT IF ALREADY WAS CONNECTED(on refresh for instance))
  //useEffect(() => {
  //  async function connect() {
  //    const { address, status } = await getCurrentWalletConnected();
  //    setWallet(address)
  //    setStatus(status);
  //    addWalletListener();
  //  }
  //  connect();    
  //}, []);
  const [walletAddress, setWallet] = useState("");    //users wallet address
  const [status, setStatus] = useState("");  //User replies with string written to contract
  const connectWalletPressed = async () => {
    const walletResponse = await connectWallet();
    setStatus(walletResponse.status);
    setWallet(walletResponse.address);
  };
  function addWalletListener() {
    if (window.ethereum) {
      window.ethereum.on("accountsChanged", (accounts) => {
        if (accounts.length > 0) {
          setWallet(accounts[0]);
          setStatus("üëÜüèΩ Mint a new NFT here");
        } else {
          setWallet("");
          setStatus("ü¶ä Connect to Metamask using the top right button.");
        }
      });
    } else {
      setStatus(
        <p>
          {" "}
          ü¶ä{" "}
          <a target="_blank" href={`https://metamask.io/download.html`} rel="noreferrer">
            You must install Metamask, a virtual Ethereum wallet, in your
            browser.
          </a>
        </p>
      );
    }
  }

  // window.addEventListener("scroll", function () {
  //   var value = window.scrollY;
  //   if (value > 100) {
  //     document.querySelector(".navbar").classList.add("bg-dark");
  //   } else {
  //     document.querySelector(".navbar").classList.remove("bg-dark");
  //   }
  // });

  return (
    <Styles>
      <Navbar expand="lg" expanded={expanded}>
        <Navbar.Brand href="/">GŒûMŒûSIS</Navbar.Brand>
        <Nav>
          <Nav.Item>
            <Button id="walletButton" onClick={connectWalletPressed}> 
            {walletAddress.length > 0 ? (
              "Connected: " +
              String(walletAddress).substring(0, 6) +
              "..." +
              String(walletAddress).substring(38)
              ) : (
              <span>ü¶ä Connect Wallet</span>
              )}
            </Button>
          </Nav.Item>
        </Nav>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" onClick={() => setExpanded(expanded ? false : "expanded")}>
          { expanded ? <AiOutlineClose color="white" /> : <FaBars color="white" /> }
          </Navbar.Toggle>
        <Navbar.Collapse id="responsive-navbar-nav">
          <Nav>
            <Scrollspy items={['home', 'about', 'minter', 'roadmap', 'team']} currentClassName="is-current">
              <Nav.Item>
                <Link to="/" onClick={() => { window.scrollTo(0, 0); setExpanded(false) }}>
                  Home
                </Link>
              </Nav.Item>
              <Nav.Item>
                <Link to="#about" onClick={() => setExpanded(false)}>
                  About
                </Link>
              </Nav.Item>
              <Nav.Item>
                <Link to="#minter" onClick={() => setExpanded(false)}>
                  Minting
                </Link>
              </Nav.Item>
              <Nav.Item>
                <Link to="#roadmap" onClick={() => setExpanded(false)}>
                  Roadmap
                </Link>
              </Nav.Item>
              <Nav.Item>
                <Link to="#team" onClick={() => setExpanded(false)}>
                  Team
                </Link>
              </Nav.Item>
              <Nav.Item>
                <a href="https://twitter.com/gemesis_" target="_blank" rel="noopener noreferrer" onClick={() => setExpanded(false)}>
                  <FaTwitter />
                </a>
              </Nav.Item>
              <Nav.Item>
                <a href="https://discord.gg/e58sqmsn" target="_blank" rel="noopener noreferrer" onClick={() => setExpanded(false)}>
                  <FaDiscord />
                </a>
              </Nav.Item>

            </Scrollspy>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    </Styles >
  )
}


export default NavbarComp;
